import React, {useState} from 'react';

const ColorButton = () => {

const [state, setState] = useState(
    {
        color: 'red',
        className: 'btn-danger',
    }
)
const changeColor = () => {
    setState(
        {
            lable: 'blue',
            className: 'btn-primary',
        }
    )

}
    return (
        <button 
        onClick={changeColor}
        className={`btn ${state.className}`}>{state.color}</button>
    )
}

export default ColorButton;
